const eventList = {
  width: "700px",
  margin: "auto",
  borderRadius: "15px",
  boxShadow: "5px #888888",
  backgroundColor: "white",
}

const listContainer = {
  backgroundColor: "linear-gradient(#f4f0f3, #f2f0f5, #f0f1f7, #edf2f7, #eaf3f7)"
}

export const styles = {
  eventList: eventList,
  listContainer: listContainer
}
