}

const listContainer = {
  backgroundColor: 'linear-gradient(#f4f0f3, #f2f0f5, #f0f1f7, #edf2f7, #eaf3f7)'
}

const eventBox = 
  `box-shadow: 0px 1px 2px #888, 0px -1px 2px #888;
  padding: 26px 20px;
  display: flex;
  &:first-of-type {
    border-radius: 6px;
  }`;


const eventHeader = {
  flex: '0',
  paddingRight: '15px'
}

const eventFooter = {
  flex: '1'

}

