  color: '#888'
}

export const styles = {
  eventList: eventList,
  listContainer: listContainer,
  eventBox: eventBox,
  eventHeader: eventHeader,
  eventFooter: eventFooter,
  avatar: avatar,
  descriptionText: descriptionText,
  dateText: dateText
}
