import React, { useState } from "react";
import { getColonyNetworkClient, Network, ColonyRole, getBlockTime } from '@colony/colony-js';
import { Wallet } from 'ethers';
import { InfuraProvider } from 'ethers/providers';
import { getLogs } from '@colony/colony-js';
import { Filter, Log, Provider } from 'ethers/providers';
import { utils } from 'ethers';
import {Promise} from 'es6-promise';
//import { ColonyRole } from '@colony/colony-js';



interface ColonyInitValue {
    colonyNetwork: string;
    token: string;
    length: string;
    fundingPotId: string;

}

interface PayoutClaimedValue {
    colonyNetwork: string;
    token: string;
    length: string;
    fundingPotId: string;

}

interface ColonyInitialisedItem {
    name: string;
    signature: string;
    topic: string;
    values: ColonyInitValue[];
}

interface PayoutClaimedItem {
    name: string;
    signature: string;
    topic: string;
    values: {
        0: string;
        1: string;
        2: string;
        fundingPotId: string;
        token: string;
    };
}

interface PaymentObject {
    recipient: string;
    finalized: boolean;
}

interface DisplayPayoutClaimedItem {
    fundingPotId: string;
    token: string;
    userAddress: string;
}



export default class EventLogs extends React.Component {

    state = { colonyInitialisedLogs: [], payoutClaimedLogs: [] };

    async componentDidMount() {
        const MAINNET_NETWORK_ADDRESS = `0x5346D0f80e2816FaD329F2c140c870ffc3c3E2Ef`;
        const MAINNET_BETACOLONY_ADDRESS = `0x869814034d96544f3C62DE2aC22448ed79Ac8e70`;
        const provider = new InfuraProvider();

        // Get a random wallet
        const wallet = Wallet.createRandom();
        // Connect your wallet to the provider
        const connectedWallet = wallet.connect(provider);

        // Check out the logs to see the wallet address
        console.log('Wallet Address:', wallet.address);

        const networkClient = await getColonyNetworkClient(
            Network.Mainnet,
            connectedWallet,
            {
                networkAddress: MAINNET_NETWORK_ADDRESS
            },
        );

        // Get the colony client instance for the betacolony
        const colonyClient = await networkClient.getColonyClient(MAINNET_BETACOLONY_ADDRESS);


        // Get the filter for Colony Initilised
        const eventFilter = colonyClient.filters.ColonyInitialised(null, null);
        const colonyInitEventLogs: any = await getLogs(colonyClient, eventFilter);
        const colonyInitialisedLogs: ColonyInitialisedItem[] = colonyInitEventLogs.map((event: any) => colonyClient.interface.parseLog(event));

        // Get the filter for Payout Claimed
        const payoutFilter = colonyClient.filters.PayoutClaimed(null, null, null);
        const payoutClaimedEventLogs: any = await getLogs(colonyClient, payoutFilter);
        let payoutClaimedLogs: PayoutClaimedItem[] = payoutClaimedEventLogs.map((event: any) => colonyClient.interface.parseLog(event));

        //const [singleLog] = colonyInitialisedLogs;
        console.log("============ Colony Initilised ============");
        //console.log(JSON.stringify(colonyInitialisedLogs));

        console.log("============ PAYOUT CLAIMED ============");
        console.log(JSON.stringify(payoutClaimedLogs));

        this.setState({ colonyInitialisedLogs: colonyInitialisedLogs, payoutClaimedLogs: payoutClaimedLogs });

        //const [singleLog] = payoutClaimedLogs;

        //const humanReadableFundingPotId = new utils.BigNumber(
        //    singleLog.values.fundingPotId
        //).toString();

        //const {
        //    associatedTypeId,
        //} = await colonyClient.getFundingPot(humanReadableFundingPotId);

        //const recipient: PaymentObject = await colonyClient.getPayment(associatedTypeId);

        //console.log(JSON.stringify(recipient.recipient));
        let displayPayoutEvents: DisplayPayoutClaimedItem[];

        async function getPaymentAddress(singleLog: PayoutClaimedItem) {
            const humanReadableFundingPotId = new utils.BigNumber(
                singleLog.values.fundingPotId
            ).toString();

            const {
                associatedTypeId,
            } = await colonyClient.getFundingPot(humanReadableFundingPotId);

            const recipient = await colonyClient.getPayment(associatedTypeId);
            return recipient;
        }

        const asyncRes = await Promise.all(payoutClaimedLogs.map(async (singleLog) => {
            const recipent = await getPaymentAddress(singleLog);
            const displayPayoutItem: DisplayPayoutClaimedItem = {
                fundingPotId: singleLog.values.fundingPotId,
                userAddress: recipent.recipient,
                token: singleLog.values.token
            };
            debugger;
            displayPayoutEvents.push(displayPayoutItem);
        }));
    }


    render() {
        return (
            <div>
                {this.state.colonyInitialisedLogs.map((post: ColonyInitialisedItem) => (
                    <div>
                        <div>
                            <h4> {post.name} </h4>
                            <h4> {post.signature} </h4>
                        </div>
                    </div>
                ))}
                {this.state.payoutClaimedLogs.map((post: PayoutClaimedItem) => (
                    <div>
                        <div>
                            <h4> {post.name} </h4>
                            <h4> {post.name} </h4>
                        </div>
                    </div>
                ))}
            </div>
        )
    }
}
