const eventList = {
  width: "700px",
  margin: "auto",
  borderRadius: "15px",
  boxShadow: "5px #888888",
  backgroundColor: "white",
}

const listContainer = {
  backgroundColor: "linear-gradient(#f4f0f3, #f2f0f5, #f0f1f7, #edf2f7, #eaf3f7)"
}

const eventBox = {
  boxShadow: "0px 1px 2px #888, 0px -1px 2px #888",
  height: "90px",
  padding: "26px 20px",
  borderRadius: "6px"
}

const eventContent = {
  display: "flex",
  width: "auto"
}

const eventFooter = {
  width: "100%",
  float: 'left'
}

const avatar = {
  borderRadius: "100px"
}

export const styles = {
  eventList: eventList,
  listContainer: listContainer,
  eventBox: eventBox,
  eventHeader: eventHeader,
  eventFooter: eventFooter,
  avatar: avatar
}
