import styled from 'styled-components';


const eventList = {
  width: '700px',
  margin: '2% auto',
  borderRadius: '15px',
  boxShadow: '5px #888888',
  backgroundColor: 'white',
  padding: "0",

}

const listContainer = {
  backgroundColor: 'linear-gradient(#f4f0f3, #f2f0f5, #f0f1f7, #edf2f7, #eaf3f7)'
}

const eventBox = {
  boxShadow: '0px 1px 2px #888, 0px -1px 2px #888',
  padding: '26px 20px',
  display: 'flex'
}

const eventHeader = {
  flex: '0',
  paddingRight: '15px'
}

const eventFooter = {
  flex: '1'

}

const firstItem = styled.div`
      &:first-of-type {
        color: red;
      }
`;

const avatar = {
  borderRadius: '100px'
}


const descriptionText = {
  fontFamily: 'Muli',
  fontSize: '14px',
  fontWeight: '400'
}

const heavyDescription = {
  fontWeight: '700'
};

const dateText = {
  fontFamily: 'Muli',
  fontSize: '12px',
  color: '#888'
}

export const styles = {
  eventList: eventList,
  listContainer: listContainer,
  eventBox: eventBox,
  eventHeader: eventHeader,
  eventFooter: eventFooter,
  avatar: avatar,
  descriptionText: descriptionText,
  dateText: dateText,
  firstItem: firstItem,
  heavyDescription: heavyDescription
}
