import React, { useState } from "react";
import { getColonyNetworkClient, Network, ColonyRole, getBlockTime } from '@colony/colony-js';
import { Wallet } from 'ethers';
import { InfuraProvider, Web3Provider } from 'ethers/providers';
import { getLogs } from '@colony/colony-js';
import { Filter, Log, Provider } from 'ethers/providers';
import { utils, Contract } from 'ethers';
import { Promise } from 'es6-promise';
import ColonyNetworkClient from '../clients/ColonyNetworkClient';
import LogsService from './LogsService';
import { PayoutClaimedItem } from "../interfaces/PayoutClaimedItem";
import { DisplayPayoutClaimedItem } from '../interfaces/DisplayPayoutClaimedItem';
import { getBalancesForEthereumAddress } from 'ethereum-erc20-token-balances-multicall';
import { MAINNET_NETWORK_ADDRESS, MAINNET_BETACOLONY_ADDRESS } from '../helpers/constants';



export default class PayoudClaimedService {

    private colonyClient: any;
    private networkClient: any;

    constructor() {
        this.networkClient = new ColonyNetworkClient();
    }

    public async prepareLogs() {

        try {

            this.colonyClient = await this.networkClient.getClient();

            const logsService = new LogsService();

            const parsedLogs: PayoutClaimedItem[] = await logsService.getParsedLogs(this.colonyClient.filters.PayoutClaimed(null, null, null));
            let displayItems: DisplayPayoutClaimedItem[];


            async function getAssoitaedTypeId(singleLog: PayoutClaimedItem, colonyClient: any) {
                const humanReadableFundingPotId = new utils.BigNumber(
                    singleLog.values.fundingPotId
                ).toString();

                const {
                    associatedTypeId,
                } = await colonyClient.getFundingPot(humanReadableFundingPotId);

                return associatedTypeId;
            }

            async function prepareDisplayItem(singleLog: PayoutClaimedItem, recipient: any) {

                const provider = new InfuraProvider();

                const abi = [
                    // Read-Only Functions
                    "function balanceOf(address owner) view returns (uint256)",
                    "function decimals() view returns (uint8)",
                    "function symbol() view returns (string)",

                    // Authenticated Functions
                    "function transfer(address to, uint amount) returns (bool)",

                    // Events
                    "event Transfer(address indexed from, address indexed to, uint amount)"
                ];

                const address = "0x0dd7b8f3d1fa88FAbAa8a04A0c7B52FC35D4312c";

                const erc20 = new Contract(address, abi, provider);
                const tokenInfo = await erc20.symbol();

                const humanReadableAmount = new utils.BigNumber(singleLog.values.amount);

                const convertedAmount = humanReadableAmount.div(humanReadableAmount.pow(18));

                return {tokenType: tokenInfo, amount: convertedAmount.toString(), userAddress: recipient}
            }


            // WORKING VERSION!

            //const promises = await parsedLogs.map(async (singleLog) => {
            //    return getAssoitaedTypeId(singleLog, this.colonyClient)
            //        .then(typeId => { return this.colonyClient.getPayment(typeId) })
            //        .then(r => Object.assign({}, singleLog, { userAddress: r.recipient }))
            //        .then(log => { return prepareDisplayItem(log) })
            //        .then(displayInfo => Object.assign({}, singleLog, { displayTokenType: displayInfo.tokenType,  displayAmount: displayInfo.amount}))
            //});

            const promises = await parsedLogs.map(async (singleLog) => {
                return getAssoitaedTypeId(singleLog, this.colonyClient)
                    .then(typeId => { return this.colonyClient.getPayment(typeId) })
                    .then(result => { return prepareDisplayItem(singleLog, result.recipient) })
                    .then(displayInfo => displayItems.push(displayInfo));
            });



            const preparedLogs = await Promise.all(promises);
            return preparedLogs;
        } catch (error) {
            console.log(error)
        }
    }


}
